mapper.insert:
insert into
(openid, name, username, userimg, sex, age, phone, idcode, city, first_date, usercategory_id, proportion, email, section, remark, source, address ) values ( #{OPENID}, #{NAME}, #{USERNAME}, #{USERIMG}, #{SEX}, #{AGE}, #{PHONE}, #{IDCODE}, #{CITY}, #{FIRST_DATE}, #{USERCATEGORY_ID}, #{PROPORTION}, #{EMAIL}, #{SECTION}, #{REMARK}, #{SOURCE}, #{ADDRESS}
)

mapper.update:
update

set 

	name = #{NAME},
	phone = #{PHONE}

where 
uid = #{UID}

mapper.select:
select a.openid,a.name,a.username,a.userimg, a.sex, a.age, a.phone, a.idcode, a.city, a.first_date, a.usercategory_id, a.proportion, a.uid, a.email, a.remark, a.section, a.source, b.PROPORTION as categoryproportion, b.CATEGORY_NAME, b.PARENT_ID, a.address, a.attention_time, c.SUM_MONEY from tab_user a LEFT OUTER JOIN tb_usercategory b on a.usercategory_id = b.USERCATEGORY_ID LEFT OUTER JOIN tb_prestore c on a.uid = c.UID
where 1=1
and a.source=#{pd.SOURCE}
and a.attention_time > #{pd.lastStart}
and a.attention_time < #{pd.lastEnd}
and a.name LIKE CONCAT('%',#{pd.keywords},'%')
or a.city LIKE CONCAT('%',#{pd.keywords},'%')
or a.section LIKE CONCAT('%',#{pd.keywords},'%') or b.CATEGORY_NAME LIKE CONCAT('%',#{pd.keywords},'%')
or a.phone LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
order by a.attention_time desc

select a.openid,a.name,a.username,a.userimg, a.sex, a.age, a.phone, a.idcode, a.city, a.first_date, a.usercategory_id, a.proportion, a.uid, a.email, a.remark, a.section, b.PROPORTION as categoryproportion, b.CATEGORY_NAME, b.PARENT_ID, a.address, a.attention_time from tab_user a LEFT OUTER JOIN tb_usercategory b on a.usercategory_id = b.USERCATEGORY_ID
where 1=1 and a.uid not in ( select uid from tab_user_discount_group where discount_group_id = #{pd.GROUP_ID})
and first_date between #{pd.first_date} and #{pd.last_date}
and sex=#{pd.sex}
and attention_time > #{pd.lastStart}
and attention_time < #{pd.lastEnd}
and a.name LIKE CONCAT('%',#{pd.keywords},'%')
or a.city LIKE CONCAT('%',#{pd.keywords},'%')
or a.section LIKE CONCAT('%',#{pd.keywords},'%') or b.CATEGORY_NAME LIKE CONCAT('%',#{pd.keywords},'%')
or a.phone LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
order by a.attention_time asc

select a.openid,a.name,a.username,a.userimg, a.sex, a.age, a.phone, a.idcode, a.city, a.first_date, a.usercategory_id, a.proportion, a.uid,b.SUM_MONEY,c.remain_money,c.remain_points,d.CATEGORY_NAME,d.PROPORTION as category_porportion from tab_user a LEFT JOIN tb_prestore b on a.uid = b.UID LEFT JOIN tb_custom_stored c on a.uid =c.uid LEFT JOIN tb_usercategory d on a.usercategory_id=d.USERCATEGORY_ID where 1=1
and ( a.phone LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.username LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
