mapper.update:
update
set UID = #{UID}, STORE_ID = #{STORE_ID}, SERVICECOST_ID = #{SERVICECOST_ID}, SERVICE_STAFF_ID = #{SERVICE_STAFF_ID}, CREATE_TIME = #{CREATE_TIME}, ORDER_STATUS = #{ORDER_STATUS}, URL = #{URL}, RECOMMEND_TIME = #{RECOMMEND_TIME}, EXPIRE_TIME = #{EXPIRE_TIME}, REMARK = #{REMARK}, STAFF_ID = #{STAFF_ID}, ORDER_MONEY = #{ORDER_MONEY}, PROPORTION = #{PROPORTION}, CREDIT = #{CREDIT}, DISCOUNT_ID = #{DISCOUNT_ID}, PAY_MONEY = #{PAY_MONEY}, REFUND = #{REFUND}, WECHAT_NAME = #{WECHAT_NAME}, WECHAT_PHONE = #{WECHAT_PHONE}
where ORDER_ID = #{ORDER_ID}

mapper.select:
select a.*, b.STORE_NAME, d.name, d.phone, p.PNAME from TB_ORDER a LEFT JOIN TB_STORE b on a.STORE_ID = b.STORE_ID LEFT JOIN service_cost e on a.SERVICECOST_ID=e.SERVICECOST_ID LEFT JOIN service_project p on e.PID = p.PROJECT_ID LEFT JOIN tab_user d on a.UID=d.uid where 1=1
and ( d.phone LIKE CONCAT('%',#{pd.keywords},'%') or d.name LIKE CONCAT('%',#{pd.keywords},'%') or a.WECHAT_NAME LIKE CONCAT('%',#{pd.keywords},'%') or a.WECHAT_PHONE LIKE CONCAT('%',#{pd.keywords},'%') or a.REMARK LIKE CONCAT('%',#{pd.keywords},'%') )
and a.SERVICE_STAFF_ID=#{pd.SERVICE_STAFF_ID}
and a.ORDER_STATUS in (2,3)
and a.ORDER_STATUS=#{pd.ORDER_STATUS}
AND a.STORE_ID = #{pd.STORE_ID}
AND a.STORE_ID = #{pd.STOREID}
order by a.CREATE_TIME desc

select a.*, b.STORE_NAME, d.name, d.phone, p.PNAME, t.DISCOUNT_AMOUNT from TB_ORDER a LEFT JOIN tb_discount t on a.DISCOUNT_ID=t.DISCOUNT_ID
LEFT JOIN TB_STORE b on a.STORE_ID = b.STORE_ID LEFT JOIN service_cost e on a.SERVICECOST_ID=e.SERVICECOST_ID LEFT JOIN service_project p on e.PID = p.PROJECT_ID LEFT JOIN tab_user d on a.UID=d.uid where a.ORDER_STATUS in (2,3)
and ( d.phone LIKE CONCAT('%',#{pd.keywords},'%') or d.name LIKE CONCAT('%',#{pd.keywords},'%') or a.WECHAT_NAME LIKE CONCAT('%',#{pd.keywords},'%') or a.WECHAT_PHONE LIKE CONCAT('%',#{pd.keywords},'%')
)
and a.ORDER_STATUS=#{pd.ORDER_STATUS}
AND a.STORE_ID = #{pd.STORE_ID}
AND a.STORE_ID = #{pd.STOREID}
order by a.CREATE_TIME desc

