mapper.select:
select a.*,b.name,b.username,b.phone,c.STORED_MONEY,d.STAFF_NAME,e.STORE_NAME from a LEFT OUTER JOIN tab_user b on a.UID = b.uid LEFT OUTER JOIN tb_stored_category c on a.STORED_CATEGORY_ID = c.STOREDCATEGORY_ID LEFT OUTER JOIN tb_staff d on a.STAFF_ID = d.STAFF_ID LEFT OUTER JOIN tb_store e on a.STORE_ID = e.STORE_ID where a.STATUS=0 and a.TYPE in('0','1','3')
and (a.CREATE_TIME > #{pd.firstDate} or a.CREATE_TIME=#{pd.firstDate})
and a.CREATE_TIME < date_add(#{pd.lastDate},interval 1 day)
and ( b.name LIKE CONCAT('%',#{pd.keywords},'%') or b.phone LIKE CONCAT('%',#{pd.keywords},'%') or b.username LIKE CONCAT('%',#{pd.keywords},'%') )
and a.STORE_ID in
#{pd.storeIds[${index}]}
and a.STORE_ID = #{pd.store}
and a.STAFF_ID = #{pd.staffName}
order by a.CREATE_TIME DESC

select a.*,b.name,b.username,b.phone,c.STORED_MONEY,d.STAFF_NAME,e.STORE_NAME from a LEFT OUTER JOIN tab_user b on a.UID = b.uid LEFT OUTER JOIN tb_stored_category c on a.STORED_CATEGORY_ID = c.STOREDCATEGORY_ID LEFT OUTER JOIN tb_staff d on a.STAFF_ID = d.STAFF_ID LEFT OUTER JOIN tb_store e on a.STORE_ID = e.STORE_ID where a.STATUS=0 and a.TYPE in('0','1','3')
and (a.CREATE_TIME > #{firstDate} or a.CREATE_TIME=#{firstDate})
and a.CREATE_TIME < date_add(#{lastDate},interval 1 day)
and ( b.name LIKE CONCAT('%',#{keywords},'%') or b.phone LIKE CONCAT('%',#{keywords},'%') or b.username LIKE CONCAT('%',#{keywords},'%') )
and a.STORE_ID in
#{storeIds[${index}]}
and a.STORE_ID = #{store}
and a.STAFF_ID = #{staffName}
order by a.CREATE_TIME DESC
